import css from "styled-components";

const styles = css`
  height: 410px;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 1px solid black;
  margin: 50px;
  background-color: ${(props) => props.theme.primaryColor};
  color: ${(props) => props.theme.secondaryColor};
`;
export default styles;
