import { css } from "styled-components";

const styles = css`
  height: 410px;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 1px solid black;
  margin: ${(props) => props.theme.spacing.ELEM_SPACING.XXXL};
  background-color: ${(props) => props.theme.colors.gray[300]};
`;
export default styles;
