import React from "react";
import App from "next/app";
import { ThemeProvider } from "styled-components";
// import { Header } from "../components/features/content";
import { blueTheme, redTheme } from "../theme/themes";

class burger_app extends App {
  constructor(props) {
    super(props);
    this.state = { theme: blueTheme };
    this.toggleTheme = this.toggleTheme.bind(this);
  }

  toggleTheme() {
    console.log("Current Theme", this.state.theme.name);
    if (this.state.theme.name === "red") {
      this.setState({ theme: blueTheme });
    } else {
      this.setState({ theme: redTheme });
    }
  }

  render() {
    console.log("Render burger_app!!");
    const { Component, pageProps } = this.props;
    return (
      <ThemeProvider theme={this.state.theme}>
        <title>My Burger App</title>
        {/* <Header toggleTheme={this.toggleTheme} /> */}
        <Component {...pageProps} />
        {/* <Footer /> */}
      </ThemeProvider>
    );
  }
}

export default burger_app;
