import styled, { css } from "styled-components";

export const ToggleWrapper = styled.div`
  width: 50px;
  min-width: 50px;
  height: 25px;
  border-radius: 25px;
  border: 1px solid #666;
  margin: auto;
  display: flex;
  background: black;
`;

export const Notch = styled.div`
  width: 21px;
  height: 21px;
  border: 1px solid #666;
  margin-top: 1px;
  background: white;
  border-radius: 50%;
  /* transition: transform 0.1s linear; */
  transform: translate(${(props) => (props.active ? "26px" : "10px")});
`;
